mapper.select:
SELECT  a.game_no, a.user_no, a.score, b.rank, b.game_data
FROM 	tb_game_user a,
        (select user_no, game_data, score,
            rank() OVER (ORDER BY score) as rank
        from tb_game_user
        where game_no = #{gameNo}
        ) b
WHERE 	a.game_no = #{gameNo}
AND     a.user_no = #{userNo}
AND	a.user_no = b.user_no

mapper.update:
UPDATE tb_game_user SET
    score = #{score},
    last_update = now()
WHERE 	game_no = #{gameNo}
AND     user_no = #{userNo}

UPDATE tb_game_user SET
game_data = #{gameData}
WHERE 	game_no = #{gameNo}
AND     user_no = #{userNo}

mapper.select:
SELECT game_no, game_nm, game_url, game_cash
FROM tb_game
WHERE 	game_no = #{gameNo}
